kickstart.firstboot_shell_cmd:
sed -i '/^exit*/i /vmfs/volumes/datastore1/configpost.sh' /etc/rc.local.d/local.sh;
sed -i 's/PasswordAuthentication no/PasswordAuthentication yes/g' /etc/ssh/sshd_config;
touch /vmfs/volumes/datastore1/configpost.sh;
chmod 755 /vmfs/volumes/datastore1/configpost.sh;
echo 'esxcfg-vswitch -p "VM Network" -v 1611 vSwitch0' >> /vmfs/volumes/datastore1/configpost.sh;
echo 'esxcli network ip dns server add --server=172.16.11.4' >> /vmfs/volumes/datastore1/configpost.sh;
echo 'esxcli network ip dns search add --domain=sfo.rainpole.io' >> /vmfs/volumes/datastore1/configpost.sh;
echo 'esxcfg-advcfg -s sfo01-m01-esx01.sfo.rainpole.io /Misc/hostname' >> /vmfs/volumes/datastore1/configpost.sh;
echo 'esxcli system hostname set -H=sfo01-m01-esx01' >> /vmfs/volumes/datastore1/configpost.sh;
echo 'esxcli system hostname set -f=sfo01-m01-esx01.sfo.rainpole.io' >> /vmfs/volumes/datastore1/configpost.sh;
echo 'echo "server 172.16.11.253" >> /etc/ntp.conf' >> /vmfs/volumes/datastore1/configpost.sh;
echo 'chkconfig ntpd on' >> /vmfs/volumes/datastore1/configpost.sh;
echo 'esxcfg-vswitch -p "Management Network" -v 1611 vSwitch0' >> /vmfs/volumes/datastore1/configpost.sh;
echo 'esxcfg-vmknic -d "Private Network"' >> /vmfs/volumes/datastore1/configpost.sh;
echo 'esxcfg-vswitch -D "Private Network" vSwitch0' >> /vmfs/volumes/datastore1/configpost.sh;
echo 'esxcli network ip interface ipv4 set -i vmk0 -I 172.16.11.101 -N 255.255.255.0 -g 172.16.11.253 -t static' >> /vmfs/volumes/datastore1/configpost.sh;
echo 'esxcfg-route 172.16.11.253' >> /vmfs/volumes/datastore1/configpost.sh;
echo 'sed -i '/configpost.sh/d' /etc/rc.local.d/local.sh' >> /vmfs/volumes/datastore1/configpost.sh;
echo 'cd /etc/vmware/ssl' >> /vmfs/volumes/datastore1/configpost.sh;
echo '/sbin/generate-certificates' >> /vmfs/volumes/datastore1/configpost.sh;
echo '/etc/init.d/hostd restart && /etc/init.d/vpxa restart' >> /vmfs/volumes/datastore1/configpost.sh;
